step.script:
var messageContent=connectorMessage.getRawData();
var account=JSON.parse(messageContent);
var dbConn ;
try{
 dbConn = DatabaseConnectionFactory.createDatabaseConnection($('DB_DRIVER'), 'jdbc:mysql://' + $('DB_HOST') + ':' + $('DB_PORT') + '/' + $('DB_NAME'), $('DB_USERNAME'), $('DB_PASSWORD'));
    var expression = " SELECT * FROM ehr_account WHERE id = '" + account.accountId + "';";
    logger.info(expression);
    var resultSet = dbConn.executeCachedQuery(expression);
    var exp2 = "";
    var resultSet2 = null;
    if(resultSet.next()) {
        exp2 = "SELECT * FROM ehr_auth_detail at,ehr_practice_detail ep where at.ehr_account_id='" + resultSet.getString("id") + "' and ep.ehr_account_id='" + resultSet.getString("id") + "';";
        //    	logger.info(exp2);
        resultSet2 = dbConn.executeCachedQuery(exp2);
        var account = {};
        account.practiceId = resultSet.getString("practice_id");
        account.accountName = resultSet.getString("account_name");
        account.departmentId = null;
        account.clientSecret = null;
        account.clientId = null;
        account.ehrType = null;
        account.thAppointmentId = null;

        if (resultSet2.next()) {
            account.clientSecret = resultSet2.getString("ehr_secret_key");
            account.clientID = resultSet2.getString("ehr_app_key");
            account.EHRType = resultSet2.getString("ehr_type");

            account.package = resultSet2.getString("package");
            account.registration = resultSet2.getString("registration");
            account.duration = resultSet2.getString("duration");
            account.role = resultSet2.getString("role");

		  account.email = resultSet2.getString("email");
		  account.licenses = resultSet2.getString("licenses");
         }
         channelMap.put("account",account);
    }
} catch (e) {
    logger.info(e);
} finally {
    if (dbConn) {
        dbConn.close();
    }
}

